import { useState } from "react";

function App() {
  const [input, setInput] = useState("");
  const [response, setResponse] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userInput = input.trim();
    const mode = isVoiceMode ? "talk" : "chat"; // Assuming `isVoiceMode` is a variable that tracks whether it's talk or chat mode

    if (!userInput) return;

    try {
      const response = await fetch('http://localhost:5000/api/message', {  // Ensure correct port for Flask backend
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ input: userInput, mode: mode }),
      });

      const data = await response.json();
      setMessages(prevMessages => [...prevMessages, { user: userInput, bot: data.message }]);
      setInput("");  // Clear input after sending
    } catch (error) {
      console.error("Error:", error);
    }
  };


  return (
    <div>
      <h1>Chat with ETE</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type something"
        />
        <button type="submit">Send</button>
      </form>
      <p>{response}</p>  {/* Display the response from Flask here */}
    </div>
  );
}

export default App;
